import React, { useState, useEffect } from "react";
import fetch from './api/dataService';
import ReactTable from 'react-table';
import "./App.css";
import 'react-table/react-table.css';
import {calculateResults,getIndividualTransactions} from './calculateResults';
import {columns, totalsByColumns} from  './api/dataColumns';

function App() {
  const [transactionData, setTransactionData] = useState(null);

  useEffect(() => {
    fetch().then((data)=> {
      const results = calculateResults(data);
      setTransactionData(results);
    });
  },[]);

  if (transactionData == null) {
    return <div>Loading...</div>;
  }

  return transactionData == null ?
    <div>Loading...</div>
      :
    <div>

      <div className="container">
        <div className="row">
          <div className="col-10">
            <h2>Monthly Customer Reward Points</h2>
            //<h2>Points Rewards System Totals by Customer Months</h2>
          </div>
        </div>
        <div className="row">
          <div className="col-8">
            <ReactTable
              data={transactionData.summaryByCustomer}
              defaultPageSize={5}
              columns={columns}
              SubComponent={row => {
                return (
                  <div>

                      {getIndividualTransactions(row,transactionData).map(tran=>{
                        return <div className="container">
                          <div className="row">
                            <div className="col-8">
                              <strong>Transaction Date:</strong> {tran.transactionDt} - <strong>$</strong>{tran.amt} - <strong>Points: </strong>{tran.points}
                            </div>
                          </div>
                        </div>
                      })}

                  </div>
                )
              }}
              />
            </div>
          </div>
        </div>

        <div className="container">
          <div className="row">
            <div className="col-10">
              <h2>Each Customer Rewards</h2>
            </div>
          </div>
          <div className="row">
            <div className="col-8">
              <ReactTable
                data={transactionData.totalPointsByCustomer}
                columns={totalsByColumns}
                defaultPageSize={5}
              />
            </div>
          </div>
        </div>
    </div>
  ;
}

export default App;
